mapper.insert:

INSERT INTO comtnorgnztinfo
			(
			ORGNZT_ID,
			ORGNZT_NM
			)

	   VALUES (
	   		  #{orgnztId},
	   		  #{orgnztNm}
	   		  )

mapper.update:

UPDATE comtnorgnztinfo
SET ORGNZT_NM = #{orgnztNm}
WHERE ORGNZT_ID = #{orgnztId}

mapper.select:
select cp.user_nm, cp.emplyr_id, co.orgnzt_nm, cp.ofcps_nm, cp.empl_no, cp.email_adres, cp.phone_number from comtnemplyrinfo cp join comtnorgnztinfo co on cp.orgnzt_id = co.orgnzt_id where cp.emplyr_id != 'TEST1' and cp.emplyr_id != 'webmaster'
and orgnzt_id #{orgnztId}
order by empl_no asc
