mapper.insert:

	SELECT TO_CHAR(SYSDATE, 'YYYYMMDDHH24MISS') ||
	LPAD(approval_doc_seq.NEXTVAL, 6, '0') FROM dual

INSERT INTO approval_doc (
doc_id, doc_title, EMPLYR_ID, doc_status,
created_dt, apprform_id, doc_html
) VALUES (
#{docId}, #{docTitle},
#{emplId}, #{docStatus}, #{createdDt}, #{apprformId},
#{docHtml}
)

INSERT INTO
approval_line (
line_id, doc_id, approver_id, line_order, line_status
)
VALUES (
approval_line_seq.NEXTVAL, #{docId}, #{approverId},
#{lineOrder}, #{lineStatus}
)

INSERT INTO
approval_cc (
cc_id, doc_id, EMPLYR_ID
) VALUES (
approval_cc_seq.NEXTVAL, #{docId}, #{emplyrId}
)

mapper.update:
UPDATE approval_line
SET line_status = '승인',
line_opinion = #{opinion},
line_dt = SYSDATE
WHERE doc_id = #{docId}
AND approver_id = #{approverId}

UPDATE approval_line
SET line_status = '반려',
line_opinion = #{opinion},
line_dt = SYSDATE
WHERE doc_id = #{docId}
AND approver_id = #{approverId}

UPDATE approval_doc
SET doc_status = '승인'
WHERE doc_id = #{docId}
AND NOT EXISTS (
SELECT 1 FROM approval_line l
WHERE l.doc_id = #{docId}
AND line_status != '승인'
)

UPDATE approval_doc
SET doc_status = '반려'
WHERE doc_id = #{docId}
